import React, { useState } from 'react';
import { FilterCriteria } from '../types';

interface FilterFormProps {
    onFilter: (filters: FilterCriteria) => void;
}

const FilterForm: React.FC<FilterFormProps> = ({ onFilter }) => {
    const [dateRange, setDateRange] = useState<[string, string]>(['', '']);
    const [revenueRange, setRevenueRange] = useState<[number, number]>([0, Infinity]);
    const [netIncomeRange, setNetIncomeRange] = useState<[number, number]>([0, Infinity]);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onFilter({ dateRange, revenueRange, netIncomeRange });
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div className="flex space-x-4">
                <div className="flex-1">
                    <label className="block text-sm font-medium text-gray-700">Start Date</label>
                    <input
                        type="date"
                        value={dateRange[0]}
                        onChange={(e) => setDateRange([e.target.value, dateRange[1]])}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                </div>
                <div className="flex-1">
                    <label className="block text-sm font-medium text-gray-700">End Date</label>
                    <input
                        type="date"
                        value={dateRange[1]}
                        onChange={(e) => setDateRange([dateRange[0], e.target.value])}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                </div>
            </div>
            <div className="flex space-x-4">
                <div className="flex-1">
                    <label className="block text-sm font-medium text-gray-700">Min Revenue</label>
                    <input
                        type="number"
                        value={revenueRange[0]}
                        onChange={(e) => setRevenueRange([Number(e.target.value), revenueRange[1]])}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                </div>
                <div className="flex-1">
                    <label className="block text-sm font-medium text-gray-700">Max Revenue</label>
                    <input
                        type="number"
                        value={revenueRange[1]}
                        onChange={(e) => setRevenueRange([revenueRange[0], Number(e.target.value)])}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                </div>
            </div>
            <div className="flex space-x-4">
                <div className="flex-1">
                    <label className="block text-sm font-medium text-gray-700">Min Net Income</label>
                    <input
                        type="number"
                        value={netIncomeRange[0]}
                        onChange={(e) => setNetIncomeRange([Number(e.target.value), netIncomeRange[1]])}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                </div>
                <div className="flex-1">
                    <label className="block text-sm font-medium text-gray-700">Max Net Income</label>
                    <input
                        type="number"
                        value={netIncomeRange[1]}
                        onChange={(e) => setNetIncomeRange([netIncomeRange[0], Number(e.target.value)])}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                </div>
            </div>
            <div className="text-right">
                <button
                    type="submit"
                    className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                    Apply Filters
                </button>
            </div>
        </form>
    );
};

export default FilterForm;
