// filepath: /E:/Personal Projects/Financial-Data-Filtering-App/financial-data-filtering-app/src/components/SortButtons.tsx
import React from 'react';

interface SortButtonsProps {
    onSort: (column: string, order: 'asc' | 'desc') => void;
}

const SortButtons: React.FC<SortButtonsProps> = ({ onSort }) => {
    return (
        <div className="flex space-x-4">
            <button
                onClick={() => onSort('date', 'asc')}
                className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
                Date Asc
            </button>
            <button
                onClick={() => onSort('date', 'desc')}
                className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
                Date Desc
            </button>
            <button
                onClick={() => onSort('revenue', 'asc')}
                className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
                Revenue Asc
            </button>
            <button
                onClick={() => onSort('revenue', 'desc')}
                className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
                Revenue Desc
            </button>
            <button
                onClick={() => onSort('netIncome', 'asc')}
                className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
                Net Income Asc
            </button>
            <button
                onClick={() => onSort('netIncome', 'desc')}
                className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
                Net Income Desc
            </button>
        </div>
    );
};

export default SortButtons;
